import React, {useState} from 'react'
import History from "./History";


const Calc = () => {
    const [value1, setValue1] = useState('')
    const [value2, setValue2] = useState('')
    const [operator, setOperator] = useState('')
    const [result,   setResult] = useState('')


    const InitValue1 = (e) => {
        setValue1(e.target.value)
    }
    const InitValue2 = (e) => {
        setValue2(e.target.value)
    }
    const Operator = (e) => {
        e.preventDefault()
        setOperator(e.target.value)
    }

    const Calculate = (e) => {
        e.preventDefault()
        const requestOptions = {
            mode: 'no-cors',
            method: 'GET',
            // redirect: 'follow',
            headers: {
                "Accept": "application/json",
                'Content-Type': 'application/json'
            },
        };
         fetch(`http://localhost:5555/${operator}/?x=${value1}&y=${value2}`, requestOptions)
             .then(response => {
                 response.text()
                 console.log(response.text())
             })
             // .then(result => console.log(result))
             .catch(error => console.log('error', error));
        setValue1('')
        setValue2('')
    }

    return (
        <div>
            <form id='data'>
                <input onChange={InitValue1}  value={value1} type="number" placeholder='input you first value'/>
                <select value={operator} onChange={Operator}>
                    <option value="addition">+</option>
                    <option value="deduction">-</option>
                    <option value="multiplication">*</option>
                    <option value="division">/</option>
                </select>
                <input value={value2} onChange={InitValue2} type="number" placeholder='input yur second value'/>
                <button onClick={Calculate}> = </button>
                <span>{result}</span>
              <History/>
            </form>

        </div>
    )
}
export default Calc
