import React, {useState} from 'react'
//Components
import History from "./History";
//Styles
import './calc.css'


const Calc = () => {
    const [value1, setValue1] = useState('')
    const [value2, setValue2] = useState('')
    const [operator, setOperator] = useState('addition')
    const [result,   setResult] = useState('')
    const [history, setHistory] = useState([])


    const InitValue1 = (e) => {
        setValue1(e.target.value)
    }
    const InitValue2 = (e) => {
        setValue2(e.target.value)
    }
    const Operator = (e) => {
        e.preventDefault()
        setOperator(e.target.value)
    }

    const Calculate = (e) => {
        e.preventDefault()
        const requestOptions = {
            method: 'GET',
            redirect: 'follow',
            headers: {
                "Accept": "application/json",
            },
        };
         fetch(`http://localhost:5555/${operator}/?x=${value1}&y=${value2}`, requestOptions)
             .then(response => response.text())
             .then(result => setResult(result))
             .catch(error => console.log('error', error));

        const requestOptionsH = {
            method: 'GET',
            headers: {
                "Accept": "application/json",
            },
            redirect: 'follow'
        };

        fetch("http://localhost:5555/history", requestOptionsH)
            .then(response => response.json())
            .then(result => setHistory(result.records))
            .catch(error => console.log('error', error));

        setValue1('')
        setValue2('')
    }
    return (
        <div>
            <form className='calculation'>
                <input onChange={InitValue1}  value={value1} type="number" placeholder='input you first value'/>
                <select value={operator} onChange={Operator}>
                    <option value="addition">+</option>
                    <option value="deduction">-</option>
                    <option value="multiplication">*</option>
                    <option value="division">/</option>
                </select>
                <input value={value2} onChange={InitValue2} type="number" placeholder='input yur second value'/>
                <button className='result_button' onClick={Calculate}> = </button>
                <span className='result'>{result}</span>
                <h1 className='title'>History List</h1>
                <div className='history_wrapper'>
                    {history.map(item => <History setvalue1={setValue1} setvalue2={setValue2} setoperation={setOperator} setHistory={setHistory} key={item.id} items={item}/>)}
                </div>
            </form>

        </div>
    )
}
export default Calc
