import React, {useState} from 'react'
import History from "./History";


const Calc = () => {
    const [value1, setvalue1] = useState(null)
    const [value2, setvalue2] = useState(null)
    const [operator, setoperator] = useState('+')


    const InitValue1 = (e) => {
        e.preventDefault()
        setvalue1(e.target.value)
    }
    const InitValue2 = (e) => {
        e.preventDefault()
        setvalue2(e.target.value)
    }
    const Operator = (e) => {
        e.preventDefault()
        let v = setoperator(e.target.value)

        console.log(v)
    }

    const Calculate = (e) => {
        e.preventDefault()
        console.log(e)

        const requestOptions = {
            method: 'GET',
            redirect: 'follow'
        };
        fetch("http://localhost:5555/addition/?x=7&y=42", requestOptions)
            .then(response => response.text())
            .then(result => console.log(result))
            .catch(error => console.log('error', error));
    }


    return (
        <div>
            <form id='data'>
                <input value={value1} onChange={InitValue1} type="text" placeholder='input you first value'/>
                <select value={operator} onChange={Operator} form='data'>
                    <option value="addition">+</option>
                    <option value="deduction">-</option>
                    <option value="multiplication">*</option>
                    <option value="division">/</option>
                </select>
                <input value={value2} onChange={InitValue2} type="text" placeholder='input yur second value'/>
                <input onClick={Calculate}  type="submit" value='='/>
                <span>result</span>
              <History/>
            </form>

        </div>
    )
}
export default Calc
